html:
{% set batch = doc %}

<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Broiler Batch Performance Report</title>
    <style>
        /* Base styles with PDF compatibility in mind */
        body {
            font-family: Arial, Helvetica, sans-serif; /* Use basic fonts for PDF compatibility */
            margin: 0;
            padding: 0;
            font-size: 10px; /* Small base font size for PDF */
            width: 100%;
        }
        
        .container {
            width: 100%;
            max-width: 100%; /* Use full width for PDF */
        }
        
        /* Header with reduced height */
        .header {
            background-color: #2c3e50;
            color: white;
            padding: 8px;
            margin-bottom: 10px;
        }
        
        .header h1 {
            margin: 0;
            font-size: 16px;
        }
        
        .header-meta {
            color: white;
            display: block;
            margin-top: 5px;
            font-size: 10px;
        }
        
        
        .header-meta span {
            color: white;
        }
        
        /* Compact KPI dashboard */
        .kpi-dashboard {
            width: 100%;
            margin-bottom: 10px;
            border-collapse: collapse;
        }
        
        .kpi-dashboard td {
            width: 25%; /* Force 4 columns */
            padding: 5px;
            vertical-align: top;
        }
        
        .kpi-card {
            background: white;
            border: 1px solid #ddd;
            padding: 5px;
            text-align: center;
            margin-bottom: 5px;
        }
        
        .kpi-card h3 {
            font-size: 10px;
            margin: 0 0 3px 0;
        }
        
        .kpi-value {
            font-size: 12px;
            font-weight: bold;
            margin: 3px 0;
        }
        
        .kpi-subtitle {
            font-size: 8px;
            color: #777;
        }
        
        /* Section styling */
        .section {
            background: white;
            border: 1px solid #ddd;
            padding: 8px;
            margin-bottom: 8px;
            width: 100%;
        }
        
        .section-title {
            color: #2c3e50;
            font-size: 12px;
            border-bottom: 1px solid #eee;
            padding-bottom: 5px;
            margin-top: 0;
            margin-bottom: 8px;
        }
        
        /* Simple table for info grid - more PDF friendly */
        .info-table {
            width: 100%;
            border-collapse: collapse;
        }
        
        .info-table td {
            padding: 3px;
            vertical-align: top;
            width: 25%;
        }
        
        .info-label {
            font-size: 8px;
            color: #777;
        }
        
        .info-value {
            font-size: 9px;
            font-weight: bold;
        }
        
        /* Data tables */
        table.data-table {
            width: 100%;
            border-collapse: collapse;
            font-size: 8px;
        }
        
        table.data-table th, 
        table.data-table td {
            border: 1px solid #ddd;
            padding: 4px;
            text-align: left;
        }
        
        table.data-table th {
            background-color: #f5f5f5;
            font-weight: bold;
        }
        
        /* Daily transactions table with fixed column widths */
        .daily-transactions-table {
            width: 100%;
            table-layout: fixed;
        }
        
        .daily-transactions-table th,
        .daily-transactions-table td {
            overflow: hidden;
            text-overflow: ellipsis;
        }
        
        /* Column widths for daily transactions */
        .daily-transactions-table th:nth-child(1),
        .daily-transactions-table td:nth-child(1) {
            width: 20%;
        }
        
        .daily-transactions-table th:nth-child(n+2),
        .daily-transactions-table td:nth-child(n+2) {
            width: 10%;
            text-align: right;
        }
        
        /* Action section */
        .action-section {
            background-color: #f5f5f5;
            padding: 8px;
            margin-top: 10px;
            border-left: 3px solid #3498db;
        }
        
        .action-title {
            font-size: 12px;
            margin-top: 0;
            margin-bottom: 8px;
        }
        
        .action-item {
            margin-bottom: 8px;
            font-size: 9px;
        }
        
        /* Footer */
        .footer {
            text-align: center;
            margin-top: 10px;
            font-size: 8px;
            color: #777;
        }
        
        /* Status indicators */
        .status-pill {
            display: inline-block;
            padding: 2px 5px;
            border-radius: 10px;
            font-size: 8px;
            background-color: #f5f5f5;
        }
        
        .status-pill.active { background-color: #d4edda; color: #155724; }
        .status-pill.closed { background-color: #f8d7da; color: #721c24; }
        
        /* Color indicators */
        .mortality { color: #e74c3c; }
        .culls { color: #f39c12; }
        .sales { color: #27ae60; }
        .profit { color: #3498db; }
        .loss { color: #e74c3c; }
        .feed { color: #3498db; }
        
        /* Negative value indicator */
        .negative-amount:before {
            content: "-";
        }

        /* Ensure page doesn't break inside important sections */
        @page {
            size: A4 landscape;
            margin: 0.5cm;
        }
        
        /* Watermark Styling */
        .watermark {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%) rotate(-45deg);
            z-index: 999; /* Place behind content */
            opacity: 0.12; /* Adjust opacity of the watermark image */
            pointer-events: none; /* Make the watermark non-interactive */
            width: 60%; /* Adjust width of the watermark image */
            height: auto; /* Maintain aspect ratio */
        }

        .watermark img {
            width: 100%; /* Adjust to fit container */
            height: 100%;
        }

    </style>
</head>
<body>
    
    <div class="watermark">
        <!-- Replace with the actual path to your watermark logo -->
        <img src="{{frappe.utils.get_url('/files/logop.png')}}" alt="Watermark Logo">
    </div>
    
    <div class="container">
        
        <!-- Header -->
        <div class="header">
            <h1>Broiler Batch Performance Report</h1>
            <div class="header-meta">
                <span>Batch: {{ batch.batch_name }} | </span>
                <span>Placed On: {{ batch.opening_date }} | </span>
                <span>Status: {{ batch.batch_status }}</span>
            </div>
        </div>
        
        <!-- KPI Dashboard using table layout instead of CSS grid -->
        <table class="kpi-dashboard">
            <tr>
                <td>
                    {% set mortality = batch.mortality|float %}
                    {% set place_quantity = batch.place_quantity_number_of_birds|float %}
                    <div class="kpi-card">
                        <h3>Mortality Rate</h3>
                        <div class="kpi-value mortality">{{ "%.2f"|format((mortality / place_quantity) * 100) }}%</div>
                        <div class="kpi-subtitle">{{ "%.0f"|format(mortality) }} birds</div>
                    </div>
                </td>
                <td>
                    {% set culls = batch.culls|float %}
                    <div class="kpi-card">
                        <h3>Cull Rate</h3>
                        <div class="kpi-value culls">{{ "%.2f"|format((culls / place_quantity) * 100) }}%</div>
                        <div class="kpi-subtitle">{{ "%.0f"|format(culls) }} birds</div>
                    </div>
                </td>
                <td>
                    {% set sale_quantity = batch.sale_quantity|float %}
                    <div class="kpi-card">
                        <h3>Sale-to-Bird Ratio</h3>
                        <div class="kpi-value sales">{{ "%.2f"|format((sale_quantity / place_quantity) * 100) }}%</div>
                        <div class="kpi-subtitle">{{ "%.0f"|format(sale_quantity) }} birds sold</div>
                    </div>
                </td>
                <td>
                    {% set total_feed = batch.total_feed|float %}
                    <div class="kpi-card">
                        <h3>Total Feed</h3>
                        <div class="kpi-value feed">{{ "%.0f"|format(total_feed) }} kg</div>
                        <div class="kpi-subtitle">Per Bird: {{ "%.2f"|format(total_feed / place_quantity) }} kg</div>
                    </div>
                </td>
            </tr>
            <tr>
                <td>
                    {% set avg_weight = 2.5 %}
                    {% set fcr = total_feed / (sale_quantity * avg_weight) if sale_quantity > 0 else 0 %}
                    <div class="kpi-card">
                        <h3>Feed Conversion Ratio</h3>
                        <div class="kpi-value">{{ "%.2f"|format(fcr) }}</div>
                        <div class="kpi-subtitle">kg feed per kg meat</div>
                    </div>
                </td>
                <td>
                    {% set batch_age = batch.batch_age_in_days|float %}
                    {% set daily_growth = (avg_weight / batch_age) if batch_age > 0 else 0 %}
                    <div class="kpi-card">
                        <h3>Daily Growth Rate</h3>
                        <div class="kpi-value">{{ "%.2f"|format(daily_growth * 1000) }} g</div>
                        <div class="kpi-subtitle">Average daily weight gain</div>
                    </div>
                </td>
                <td>
                    {% set rate = batch.rate|float %}
                    {% set bird_cost = batch.bird_cost|float %}
                    {% set profit = (sale_quantity * rate) - (bird_cost * place_quantity) %}
                    <div class="kpi-card">
                        <h3>{{ 'Profit Margin' if profit >= 0 else 'Loss Margin' }}</h3>
                        {% if profit >= 0 %}
                        <div class="kpi-value profit">₹{{ "%.2f"|format(profit) }}</div>
                        {% else %}
                        <div class="kpi-value loss negative-amount">₹{{ "%.2f"|format(abs(profit)) }}</div>
                        {% endif %}
                        <div class="kpi-subtitle">Return on Investment</div>
                    </div>
                </td>
                <td>
                    {% set investment = bird_cost * place_quantity %}
                    {% set roi_percent = (profit / investment * 100) if investment > 0 else 0 %}
                    <div class="kpi-card">
                        <h3>ROI Percentage</h3>
                        {% if roi_percent >= 0 %}
                        <div class="kpi-value profit">{{ "%.2f"|format(roi_percent) }}%</div>
                        {% else %}
                        <div class="kpi-value loss negative-amount">{{ "%.2f"|format(abs(roi_percent)) }}%</div>
                        {% endif %}
                        <div class="kpi-subtitle">Return on Investment %</div>
                    </div>
                </td>
            </tr>
        </table>
        
        <!-- Batch Overview -->
        <div class="section">
            <h2 class="section-title">Batch Overview</h2>
            <table class="info-table">
                <tr>
                    <td>
                        <div class="info-label">Batch Name</div>
                        <div class="info-value">{{ batch.batch_name }}</div>
                    </td>
                    <td>
                        <div class="info-label">Farmer</div>
                        <div class="info-value">{{ batch.farmer_name }}</div>
                    </td>
                    <td>
                        <div class="info-label">Placement Date</div>
                        <div class="info-value">{{ batch.opening_date }}</div>
                    </td>
                    <td>
                        <div class="info-label">Age</div>
                        <div class="info-value">{{ batch.batch_age_in_days }} days</div>
                    </td>
                </tr>
                <tr>
                    <td>
                        <div class="info-label">Status</div>
                        <div class="info-value">
                            <span class="status-pill {{ 'closed' if batch.batch_status == 'Completed' else 'active' }}">
                                {{ batch.batch_status }}
                            </span>
                        </div>
                    </td>
                    <td>
                        <div class="info-label">Location</div>
                        <div class="info-value">
                            {% set location_doc = frappe.get_doc('Location', batch.location) %}
                            {% set shed_doc = frappe.get_doc('Shed', batch.shed) %}
                            {{ location_doc.location_name }} / {{ shed_doc.shed_name }}
                        </div>
                    </td>
                    <td>
                        <div class="info-label">Breed</div>
                        <div class="info-value">
                            {% set breed_doc = frappe.get_doc('Breed', batch.breed_name) %}
                            {{ breed_doc.breed_name }}
                        </div>
                    </td>
                    <td>
                        <div class="info-label">Type</div>
                        <div class="info-value">
                            {% set batch_type_doc = frappe.get_doc('Batch Type', batch.batch_type) %}
                            {{ batch_type_doc.batch_type }}
                        </div>
                    </td>
                </tr>
            </table>
        </div>
        
        <!-- Population Metrics -->
        <div class="section">
            <h2 class="section-title">Population Metrics</h2>
            <table class="info-table">
                <tr>
                    <td>
                        <div class="info-label">Initial Placement</div>
                        <div class="info-value">{{ "%.0f"|format(place_quantity) }}</div>
                    </td>
                    <td>
                        <div class="info-label">Current Live Birds</div>
                        <div class="info-value">{{ "%.0f"|format(batch.live_quantity_number_of_birds|float) }}</div>
                    </td>
                    <td>
                        <div class="info-label">Total Mortality</div>
                        <div class="info-value">{{ "%.0f"|format(mortality) }}</div>
                    </td>
                    <td>
                        <div class="info-label">Total Culls</div>
                        <div class="info-value">{{ "%.0f"|format(culls) }}</div>
                    </td>
                </tr>
                <tr>
                    <td>
                        <div class="info-label">Birds Sold</div>
                        <div class="info-value">{{ "%.0f"|format(sale_quantity) }}</div>
                    </td>
                    <td>
                        <div class="info-label">Livability</div>
                        <div class="info-value">{{ "%.2f"|format(((place_quantity - mortality - culls) / place_quantity) * 100) }}%</div>
                    </td>
                    <td>
                        <div class="info-label">Survival Rate</div>
                        <div class="info-value">{{ "%.2f"|format((batch.live_quantity_number_of_birds|float / place_quantity) * 100) }}%</div>
                    </td>
                    <td>
                        <div class="info-label">Mortality + Cull Rate</div>
                        <div class="info-value">{{ "%.2f"|format(((mortality + culls) / place_quantity) * 100) }}%</div>
                    </td>
                </tr>
            </table>
        </div>
        
        <!-- Financial Performance -->
        <div class="section">
            <h2 class="section-title">Financial Performance</h2>
            <table class="data-table">
                <tr>
                    <th width="50%">Metric</th>
                    <th width="50%">Value</th>
                </tr>
                <tr>
                    <td>Rate per Bird Purchase</td>
                    <td>₹{{ "%.2f"|format(rate) }}</td>
                </tr>
                <tr>
                    <td>Bird Cost</td>
                    <td>₹{{ "%.2f"|format(bird_cost) }}</td>
                </tr>
                <tr>
                    <td>Total Amount</td>
                    <td>₹{{ "%.2f"|format(batch.amount|float) }}</td>
                </tr>
                <tr>
                    <td>Biological Value</td>
                    <td>₹{{ "%.2f"|format(batch.biological_value|float) }}</td>
                </tr>
                <tr>
                    <td>Total Sales Income</td>
                    <td>₹{{ "%.2f"|format(sale_quantity * rate) }}</td>
                </tr>
                <tr>
                    <td>Total Investment</td>
                    <td>₹{{ "%.2f"|format(bird_cost * place_quantity) }}</td>
                </tr>
                <tr>
                    <td>Feed Cost (est.)</td>
                    {% set feed_cost_per_kg = 30 %}
                    <td>₹{{ "%.2f"|format(total_feed * feed_cost_per_kg) }}</td>
                </tr>
                {% if batch_type_doc.batch_type == 'CONTRACT' %}
                <tr>
                    <td>Growing Charges</td>
                    <td>₹{{ "%.2f"|format(batch.growing_charges|float) }}</td>
                </tr>
                {% endif %}
                {% set profit = (sale_quantity * rate) - (bird_cost * place_quantity) %}
                <tr>
                    <td><strong>{{ 'Net Profit' if profit >= 0 else 'Net Loss' }}</strong></td>
                    {% if profit >= 0 %}
                    <td><strong>₹{{ "%.2f"|format(profit) }}</strong></td>
                    {% else %}
                    <td><strong class="negative-amount">₹{{ "%.2f"|format(abs(profit)) }}</strong></td>
                    {% endif %}
                </tr>
            </table>
        </div>
        
        <!-- Daily Transactions -->
        <div class="section">
            <h2 class="section-title">Daily Transactions</h2>
            <table class="data-table daily-transactions-table">
                <tr>
                    <th>Date</th>
                    <th>Mort.</th>
                    <th>Culls</th>
                    <th>Weight(g)</th>
                    <th>Feed(kg)</th>
                    <th>Med.</th>
                    <th>Vac.</th>
                    <th>Vit.</th>
                </tr>
                
                {% set transactions = frappe.get_all('Broiler Daily Transaction', 
                   fields=['transaction_date', 'mortality_number_of_birds', 'culls', 'feed_consumed_quantity', 'average_bird_weight_in_grams', 'medicine_consumed_quantity', 'vaccine_consumed_quantity', 'vitamin_consumed_quantity'], 
                   filters={'batch': batch.name}, 
                   order_by='transaction_date desc',
                   limit=10) %}
                
                {% for tx in transactions %}
                <tr>
                    <td>{{ tx.transaction_date }}</td>
                    <td>{{ tx.mortality_number_of_birds or 0 }}</td>
                    <td>{{ tx.culls or 0 }}</td>
                    <td>{{ "%.1f"|format(tx.average_bird_weight_in_grams|float or 0) }}</td>
                    <td>{{ "%.1f"|format(tx.feed_consumed_quantity|float or 0) }}</td>
                    <td>{{ "%.1f"|format(tx.medicine_consumed_quantity|float or 0) }}</td>
                    <td>{{ "%.1f"|format(tx.vaccine_consumed_quantity|float or 0) }}</td>
                    <td>{{ "%.1f"|format(tx.vitamin_consumed_quantity|float or 0) }}</td>
                </tr>
                {% endfor %}
                
                {% if not transactions %}
                <tr>
                    <td colspan="8" style="text-align: center;">No daily transactions recorded</td>
                </tr>
                {% endif %}
            </table>
            
            {% if transactions and transactions|length == 10 %}
            <div style="text-align: center; font-size: 8px; margin-top: 5px;">
                <i>Showing the 10 most recent transactions. View complete history in the system.</i>
            </div>
            {% endif %}
        </div>
        
        <!-- Recommended Actions -->
        <div class="action-section">
            <h2 class="action-title">Recommended Actions</h2>
            
            {% set mortality_rate = (mortality / place_quantity) * 100 %}
            {% if mortality_rate > 3 %}
            <div class="action-item">
                <strong>Address Mortality Rate:</strong> Your mortality rate of {{ "%.2f"|format(mortality_rate) }}% is above target. Enhance biosecurity measures and review vaccination protocols.
            </div>
            {% endif %}
            
            {% set cull_rate = (culls / place_quantity) * 100 %}
            {% if cull_rate > 2 %}
            <div class="action-item">
                <strong>Reduce Culling Rate:</strong> Your culling rate of {{ "%.2f"|format(cull_rate) }}% indicates potential issues. Review genetic quality of chicks and nutrition protocols.
            </div>
            {% endif %}
            
            {% set sale_ratio = (sale_quantity / place_quantity) * 100 %}
            {% if sale_ratio < 90 %}
            <div class="action-item">
                <strong>Improve Sale-to-Bird Ratio:</strong> Your ratio of {{ "%.2f"|format(sale_ratio) }}% suggests room for improvement. Review marketing strategies.
            </div>
            {% endif %}
            
            {% set avg_weight = 2.5 %}
            {% set fcr = total_feed / (sale_quantity * avg_weight) if sale_quantity > 0 else 0 %}
            {% if fcr > 1.8 %}
            <div class="action-item">
                <strong>Optimize Feed Conversion:</strong> Your FCR of {{ "%.2f"|format(fcr) }} is higher than target. Review feed quality and feeding schedule.
            </div>
            {% endif %}
            
            {% if profit < 0 %}
            <div class="action-item">
                <strong>Address Negative Profit Margin:</strong> Your operation is currently running at a loss. Urgent review of production costs needed.
            </div>
            {% endif %}
        </div>
        
        <div class="footer">
        {% set organization = frappe.get_last_doc("Organization", filters={"organization_owner": frappe.session.user}) %}
        {% if organization %}
            Generated for {{ organization.organization_name }} | Report Date: {{ frappe.utils.nowdate() + " " + frappe.utils.nowtime() }}
        {% endif %}
        </div>
        
    </div>
</body>
</html>
